package cat.uvic.teknos.library.backoffice;

import cat.uvic.teknos.library.domain.jdbc.models.JdbcModelFactory;
import cat.uvic.teknos.library.domain.jdbc.repositories.JdbcRepositoryFactory;
import cat.uvic.teknos.library.models.ModelFactory;
import cat.uvic.teknos.library.repositories.RepositoryFactory;

public class App {

    public static void main(String[] args) {
        RepositoryFactory repositoryFactory = new JdbcRepositoryFactory();
        ModelFactory modelFactory = new JdbcModelFactory();
        var backOffice = new BackOffice(System.in, System.out, repositoryFactory, modelFactory);

        backOffice.start();
    }
}